CREATE OR REPLACE FUNCTION public.sp_cliente_get_informes(p_cli_id integer, OUT rtn refcursor)
 RETURNS refcursor
 LANGUAGE plpgsql
AS $function$
declare
   v_us_id integer;
begin

   rtn := 'rtn';

