CREATE OR REPLACE FUNCTION public.sp_doc_factura_venta_save(p_us_id integer, p_fvtmp_id integer)
 RETURNS SETOF row_result
 LANGUAGE plpgsql
AS $function$
declare
   rtn row_result;
   rtnAnticipo row_result;
   
   v_success integer;
   v_error integer;
   v_error_msg varchar(5000);
   v_cfg_valor varchar(5000);
   
   v_is_new integer;
   
   v_fv_id integer;
   v_fvi_id integer;
   
   v_orden smallint;
   v_doct_id integer;
   v_est_id integer;
